                    "publication": "{\"publisher\": \"Swiss Federal Institute for Forest, Snow and Landscape Research WSL / Swiss Competence Center for Energy Research - Biomass for Swiss Energy Transition SCCER-BIOSWEET\", \"publication_year\": \"2017\"}",
                    "metadata_modified": "2018-12-09T00:33:15.627767",
                    "author": "[{\"affiliation\": \"WSL\", \"name\": \"Oliver Thees\", \"email\": \"oliver.thees@wsl.ch\"}, {\"affiliation\": \"WSL\", \"name\": \"Vanessa Burg\", \"email\": \"vanessa.burg@wsl.ch\"}, {\"affiliation\": \"WSL\", \"name\": \"Matthias Erni\", \"email\": \"matthias.erni@wsl.ch\"}, {\"affiliation\": \"WSL\", \"name\": \"Gillianne Bowman\", \"email\": \"gillianne.bowman@wsl.ch\"}, {\"affiliation\": \"WSL\", \"name\": \"Renato Lemm\", \"email\": \"renato.lemm@wsl.ch\"}]",
